mapper.update:

update base_calendar_extend
set DEL_FLAG =  'Y' 



       and ID = #{id,jdbcType=VARCHAR}


       and YEAR = #{year,jdbcType=INTEGER}


       and MONTH = #{month,jdbcType=INTEGER}


       and DAY = #{day,jdbcType=INTEGER}


       and WORK_OR_REST = #{workOrRest,jdbcType=VARCHAR}


       and HOLIDAY = #{holiday,jdbcType=VARCHAR}


       and DATA_USER_ID = #{dataUserId,jdbcType=VARCHAR}


       and DATA_OFFICE_ID = #{dataOfficeId,jdbcType=VARCHAR}


       and DATA_TYPE = #{dataType,jdbcType=VARCHAR}


       and DATA_AREA_ID = #{dataAreaId,jdbcType=VARCHAR}


       and DEL_FLAG = #{delFlag,jdbcType=CHAR}


       and CREATE_AT = #{createAt,jdbcType=TIMESTAMP}


       and CREATE_BY = #{createBy,jdbcType=VARCHAR}


       and UPDATE_AT = #{updateAt,jdbcType=TIMESTAMP}


       and UPDATE_BY = #{updateBy,jdbcType=VARCHAR}

