update base_calendar_extend
set YEAR = #{entity.year,jdbcType=INTEGER},
  MONTH = #{entity.month,jdbcType=INTEGER},
  DAY = #{entity.day,jdbcType=INTEGER},
  WORK_OR_REST = #{entity.workOrRest,jdbcType=VARCHAR},
  HOLIDAY = #{entity.holiday,jdbcType=VARCHAR},
  DATA_USER_ID = #{entity.dataUserId,jdbcType=VARCHAR},
  DATA_OFFICE_ID = #{entity.dataOfficeId,jdbcType=VARCHAR},
  DATA_TYPE = #{entity.dataType,jdbcType=VARCHAR},
  DATA_AREA_ID = #{entity.dataAreaId,jdbcType=VARCHAR},
  DEL_FLAG = #{entity.delFlag,jdbcType=CHAR},
  CREATE_AT = #{entity.createAt,jdbcType=TIMESTAMP},
  CREATE_BY = #{entity.createBy,jdbcType=VARCHAR},
  UPDATE_AT = #{entity.updateAt,jdbcType=TIMESTAMP},
  UPDATE_BY = #{entity.updateBy,jdbcType=VARCHAR}



       and ID = #{condition.id,jdbcType=VARCHAR}


       and YEAR = #{condition.year,jdbcType=INTEGER}


       and MONTH = #{condition.month,jdbcType=INTEGER}


       and DAY = #{condition.day,jdbcType=INTEGER}


       and WORK_OR_REST = #{condition.workOrRest,jdbcType=VARCHAR}


       and HOLIDAY = #{condition.holiday,jdbcType=VARCHAR}


