update base_calendar_extend



      when id=#{item.id} then #{item.year}




      when id=#{item.id} then #{item.month}




      when id=#{item.id} then #{item.day}




      when id=#{item.id} then #{item.workOrRest}




      when id=#{item.id} then #{item.holiday}




      when id=#{item.id} then #{item.dataUserId}




      when id=#{item.id} then #{item.dataOfficeId}




      when id=#{item.id} then #{item.dataType}




      when id=#{item.id} then #{item.dataAreaId}




      when id=#{item.id} then #{item.delFlag}




      when id=#{item.id} then #{item.createAt}




      when id=#{item.id} then #{item.createBy}




      when id=#{item.id} then #{item.updateAt}




      when id=#{item.id} then #{item.updateBy}



where ID in 

  #{item.id}

mapper.select:

select 

from base_calendar_extend
 where del_flag = 'N'

  and year = #{year}


  and month = #{month}


  and day = #{day}


  and WORK_OR_REST = #{workOrRest}


  and HOLIDAY like concat('%',#{holiday},'%')
