mapper.select:

select

,

from cms_content
 where del_flag = 'N'

  and title like concat('%',#{title},'%')


  and author like concat('%',#{author},'%')


  and status = #{status}


  and site_id = #{siteId}


  and channel_id = #{channelId}


  and content like concat('%',#{content},'%')
