mapper.select:

select 

from base_message
 where del_flag = 'N'

  and title LIKE concat('%',#{title},'%')


  and msg_type = #{msgType}


  and msg_state = #{msgState}


  and msg_level = #{msgLevel}

select
  m.ID,
  m.TITLE,
  m.PROFILE,
  m.CONTENT,
  m.MSG_TYPE,
  m.MSG_LEVEL,
  tcur.is_read
from base_message m
JOIN base_message_target_client tc on tc.MESSAGE_ID = m.id
JOIN base_message_target_client_user_rel tcur on tcur.TARGET_CLIENT_ID = tc.id
where m.del_flag = 'N'
  and tc.TARGET_CLIENT = #{targetClientParamsDto.targetClient}
  and tcur.user_id = #{userId}

    and tc.sub_target_client = #{targetClientParamsDto.subTargetClient}


  and  tcur.is_read = #{isRead}


  and title LIKE concat('%',#{title},'%')


  and msg_type = #{msgType}


  and msg_level = #{msgLevel}

  ORDER BY m.UPDATE_AT desc
