mapper.select:

select

from weixin_account
where del_flag = 'N'

    and WHICH like concat('%'#{which}'%')


    and ACCOUNT like concat('%'#{account}'%')


    and NAME like concat('%'#{name}'%')


    and WEIXIN_ID like concat('%'#{weixinId}'%')


    and TOKEN = #{token}


    and APPID = #{appid}


    and APPSECRET = #{appsecret}


    and TYPE = #{type}


    and STATUS = #{status}


    and AUTH = #{auth}


    and TEMPLATE_TYPE = #{templateType}


    and TEMPLATE = #{template}


    and MSG_TYPE = #{msgType}


    and REMARK = #{remark}


    and DATA_USER_ID = #{dataUserId}


    and DATA_OFFICE_ID = #{dataOfficeId}


    and DATA_TYPE = #{dataType}


    and DATA_AREA_ID = #{dataAreaId}


    and DEL_FLAG = #{delFlag}


    and CREATE_AT = #{createAt}


    and CREATE_BY = #{createBy}


    and UPDATE_AT = #{updateAt}


    and UPDATE_BY = #{updateBy}
